-(void)viewWillAppear:(BOOL )animated
{
	[super viewWillAppear:YES];
	UIApplication *tempWin=[ UIApplication sharedApplication];//.delegate.window;
	MarioMoveAppDelegate* tempdel=tempWin.delegate;
	NSUserDefaults *pastData=[NSUserDefaults standardUserDefaults];
	NSArray *royScoreArray=[pastData objectForKey:@"royScores"];
	NSMutableArray *tempScoreArray=[NSMutableArray arrayWithArray:royScoreArray];
	int prevMinutes=tempdel.roykeController.minutes;
	int prevSeconds=tempdel.roykeController.seconds;
	int prevTimeValue= prevMinutes*60+prevSeconds;
	for (int i=0;i<10;i++)
	{
		if([royScoreArray count]==0)
			break;
		NSString * scoreString=[royScoreArray objectAtIndex:i];
		NSString *minuteString=[scoreString substringToIndex:1];
		NSString *secondString=[scoreString substringFromIndex:2];
		int minutes=[minuteString intValue];
		int seconds=[secondString intValue];
		int tempTimeValue= minutes*60 +seconds;
		NSString *newString;
		if(prevTimeValue<tempTimeValue&& prevTimeValue!=0 ||  tempTimeValue==0)
		{
			if(prevSeconds<10)
				newString=[NSString stringWithFormat:@"%d:%@",prevMinutes,[NSString stringWithFormat:@"0%d", prevSeconds]];
			else	
			newString=[NSString stringWithFormat:@"%d:%d",prevMinutes,prevSeconds];
			
			
			[tempScoreArray removeObjectAtIndex:9];
			[tempScoreArray insertObject:newString atIndex:i];
			break;
		
		}
		
		
			
	}
	
	royScoreArray=[NSArray arrayWithArray:tempScoreArray];
	[pastData setObject:royScoreArray forKey:@"royScores"];	
	NSString *futureText=@"";;
	if([royScoreArray count]!=0)
	{
	for (int i=0;i<10;i++) 
	{
		futureText=[futureText stringByAppendingString:[NSString stringWithFormat:@"%d  %@ \n",i+1, [royScoreArray objectAtIndex:i]]];
	}
	
	timesLabel.text=futureText;
		timesLabel.numberOfLines=18;
		timesLabel.adjustsFontSizeToFitWidth=YES;
		[timesLabel sizeToFit];
	}
	
	NSArray *foo=[NSArray arrayWithArray: tempdel.window.subviews];
	//NSLog("%@",foo);
	
	
	int prevTime=tempdel.roykeController.minutes*60+tempdel.roykeController.seconds;
	if (prevTime==0)
	{
		lastScore.hidden=YES;
		yourScore.hidden=YES;
		//timesView.hidden=YES;
	}
	else 
	{
		yourScore.hidden=NO;
		lastScore.text=[NSString stringWithFormat:@"%d:%d",tempdel.roykeController.minutes,tempdel.roykeController.seconds];
		lastScore.hidden=NO;
	}


}
	
	
	//NSUserDefaults *pastData=[NSUserDefaults standardUserDefaults];
	/*int prevHighScore=[pastData integerForKey:@"highscore"];
	if(prevScore>prevHighScore)
	{
		[pastData setInteger:prevScore forKey:@"highscore"];
		
	}//integerForKey:@"highscore"]!=nil)
	allTimeBest.text=[NSString stringWithFormat:@"%d",prevHighScore];
}


