-(IBAction)startJerga:(id)sender
{
	[self.view removeFromSuperview];
	UIApplication *tempWin=[ UIApplication sharedApplication];//.delegate.window;
	MarioMoveAppDelegate* tempdel=tempWin.delegate;
	
	tempdel.marioController=[[MarioMoveViewController alloc] initWithNibName:@"MarioMoveViewController"bundle:nil];
	//[tempdel.marioController release];
	[tempdel.window addSubview:tempdel.marioController.view];
	return;
}


-(IBAction)startRoy:(id)sender
{
	[self.view removeFromSuperview];
	UIApplication *tempWin=[ UIApplication sharedApplication];//.delegate.window;
	MarioMoveAppDelegate* tempdel=tempWin.delegate;
	
	tempdel.roykeController=[[RoykeController alloc] initWithNibName:@"RoykeController"bundle:nil];
	//[tempdel.marioController release];
	[tempdel.window addSubview:tempdel.roykeController.view];
	return;
}


	
	
	

